```javascript
// init libraries...

try {
	var info = await zeroPage.getSiteInfo():
	var file = await zeroFS.readFile("a");
	file = JSON.parse(file || "{}");
	file.data = file.data || {};
	file.data[file.next_id] = info;
	file.next_id++;
	file = JSON.stringify(file);
	await zeroFS.writeFile("a", file);
